mapper.select:
select *
from level_test
where lt_status = 'Y' and lt_no = #{ltNo}

mapper.update:
update level_test
set lt_title = #{ltTitle}, lt_no1 = #{ltNo1}, lt_no2 = #{ltNo2},
   lt_no3 = #{ltNo3}, lt_no4 = #{ltNo4}, lt_answer = #{ltAnswer}
where lt_no = #{ltNo}

update member
set mb_level = #{mbLevel}
where mb_id = #{mbId}
