mapper.update:
UPDATE NOTICE_BOARD
SET NOTICE_NUM = NOTICE_NUM + 1
WHERE NOTICE_NO = #{nId}

UPDATE NOTICE_BOARD
SET NOTICE_TITLE = #{noticeTitle}, NOTICE_DATE = SYSDATE, NOTICE_CONTENT = #{noticeContent}
WHERE NOTICE_NO = #{noticeNo}

UPDATE NOTICE_BOARD
SET NOTICE_STATUS = 'N'
WHERE NOTICE_NO = #{noticeNo}

mapper.select:
SELECT *
FROM OUR_CLASS_BOARD
WHERE OCB_STATUS = 'Y' AND OCB_NO = #{ocbNo}

mapper.update:
UPDATE OUR_CLASS_BOARD
SET OCB_NUM = OCB_NUM + 1
WHERE OCB_NO = #{ocbNo}

UPDATE OUR_CLASS_BOARD
SET OCB_IMAGE = #{ocbImage}, OCB_CATEGORY = #{ocbCategory}, OCB_CONTENT = #{ocbContent}, OCB_TITLE = #{ocbTitle}, OCB_DATE = SYSDATE
WHERE OCB_NO = #{ocbNo}

UPDATE OUR_CLASS_BOARD
SET OCB_STATUS = 'N'
WHERE OCB_NO = #{ocbNo}

UPDATE OUR_CLASS_REPLY
SET OCR_CONTENT = #{ocrContent}, OCR_DATE = SYSDATE
WHERE OCR_NO = #{ocrNo}

UPDATE OUR_CLASS_REPLY
SET OCR_STATUS = 'N'
WHERE OCR_NO = #{ocrNo}
